mapper.select:
select 
FROM teacher_year_student tys

WHERE tys.year = #{sysYear, jdbcType=VARCHAR}

    and et.node = #{teacherInfo.teacher.node,jdbcType=INTEGER}


    and u.name = #{teacherInfo.name,jdbcType=INTEGER}

order by et.node ASC

